        New-Item -ItemType directory -Path $path
    }

    Invoke-WebRequest -Uri ($PRESIGNED_URL.Replace('*',$MODEL_PATH+"/params.json")) -OutFile ($path+"/params.json") -UserAgent "wget"

    for ($s=0; $s -le $SHARD; $s++)
