class DefaultHandlers(object):
    def __getitem__(self, code):
        description = http_codes.get(code)
        if description is not None:
            return lambda e: '<h1>Error {}</h1>\n{}<br>{}'.format(code, description, e)
        else:
            raise KeyError(code)

